import pytest

from pytest_httpx import HTTPXMock

from offchain.metadata.adapters.ipfs import IPFSAdapter
from offchain.metadata.fetchers.metadata_fetcher import MetadataFetcher


class TestMetadataFetcher:
    def test_metadata_fetcher_register_adapter(self):  # type: ignore[no-untyped-def]
        fetcher = MetadataFetcher()
        adapter = IPFSAdapter()
        fetcher.register_adapter(adapter, "ipfs://")
        assert fetcher.sess.adapters.get("ipfs://") == adapter


@pytest.mark.asyncio
async def test_gen_fetch_data_adapter():  # type: ignore[no-untyped-def]
    fetcher = MetadataFetcher()
    content = await fetcher.gen_fetch_content(
        "data:image/svg+xml;base64,"  # noqa
    )
    assert content is not None


